import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import logo from '../../assets/images/logo.png';

const LoginPage = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e) => {
    e.preventDefault();
    // 여기에 로그인 로직을 구현할 수 있습니다
    navigate('/dashboard'); // 로그인 성공 시 대시보드로 이동
  };

  return (
    <div className="flex items-center justify-center min-h-screen bg-[#1a3b6e]">
      <div className="w-96 p-8 rounded-lg bg-white bg-opacity-10 backdrop-blur-sm">
        <div className="flex justify-center mb-8">
          <img src={logo} alt="Logo" className="w-24 h-24 object-contain" />
        </div>

        <form onSubmit={handleLogin} className="space-y-4">
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Username"
            className="w-full p-2 rounded bg-white bg-opacity-20 text-white placeholder-gray-300 outline-none"
          />

          <input
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Password"
            className="w-full p-2 rounded bg-white bg-opacity-20 text-white placeholder-gray-300 outline-none"
          />

          <button
            type="submit"
            className="w-full p-2 bg-blue-500 text-white rounded hover:bg-blue-600 transition-colors"
          >
            로그인
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginPage;
